INSERT INTO `main_visidanmisi` VALUES (1,'Masyarakat Memperoleh Kepastian Hukum','<ol>\r\n	<li>Mewujudkan Peraturan Perundang-Undangan Yang Berkualitas;</li>\r\n	<li>Mewujudkan Pelayanan Hukum Yang Berkualitas;</li>\r\n	<li>Mewujudkan Penegakan Hukum Yang Berkualitas;</li>\r\n	<li>Mewujudkan Penghormatan, Pemenuhan Dan ;Perlindungan HAM;</li>\r\n	<li>Mewujudkan Layanan Manajemen Administrasi Kementrian Hukum dan HAM; Serta</li>\r\n	<li>Mewujudkan Aparatur Kementrian Hukum dan HAM yang Profesional Dan Berintegritas</li>\r\n</ol>','Ramah dan Pasti','Janji Layanan','Kepastian Persyaratan','Kementerian Hukum dan HAM menjunjung tinggi tata nilai kami \"P-A-S-T-I\"','Setiap kegiatan dalam rangka penyelenggaraan pemerintah dapat dipertanggungjawabkan kepada masyarakat sesuai dengan ketentuan atau peraturan yang berlaku','Komitmen untuk membangun dan memastikan hubungan kerjasama yang produktif serta kemitraan yang harmonis dengan para pemangku kepentingan untuk menemukan dan melaksanakan solusi terbaik, bermanfaat, dan berkualitas','Kementerian Hukum dan HAM menjamin akses atau kebebasan bagi setiap orang untuk memperoleh informasi tentang penyelenggaraan pemerintahan, yakni informasi tentang kebijakan, proses pembuatan dan pelaksanaannya, serta hasil-hasil yang dicapai','Kementerian Hukum dan HAM mendukung kreatifitas dan mengembangkan inisiatif untuk selalu melakukan pembaharuan dalam penyelenggaraan tugas dan fungsinya.');
